import React, { useState } from "react";

// Example makeAvatar function
const makeAvatar = (name) =>
  `https://i.pravatar.cc/150?u=${name}`;

// Avatars array with responsive positions
const avatars = [
  { id: 1, name: "U1", pos: "left-[0px] top-[10px] md:left-[-2%] md:top-[40px]" },
  { id: 2, name: "U2", pos: "left-[0px] top-[80px] md:left-[18%] md:top-[80px]" },
  { id: 3, name: "U3", pos: "left-[0px] top-[150px] md:left-[10%] md:top-[200px]" },
  { id: 4, name: "U4", pos: "left-[0px] top-[220px] md:left-[35%] md:top-[70px]" },
  { id: 5, name: "U5", pos: "left-[0px] top-[290px] md:left-[52%] md:top-[70px]" },
  { id: 6, name: "U6", pos: "left-[0px] top-[360px] md:left-[25%] md:top-[230px]" },
  { id: 7, name: "U7", pos: "left-[0px] top-[430px] md:right-[20%] md:top-[100px]" },
  { id: 8, name: "U8", pos: "left-[0px] top-[500px] md:right-[5%] md:top-[80px]" },
];

export default function AvatarShowcase() {
  const [activeId, setActiveId] = useState(null);

  return (
    <div className="relative h-[700px] w-full bg-gray-100 p-6">
      {avatars.map((avatar) => (
        <div
          key={avatar.id}
          className={`absolute ${avatar.pos}`}
        >
          {/* Avatar */}
          <img
            src={makeAvatar(avatar.name)}
            alt={avatar.name}
            className="w-12 h-12 rounded-full cursor-pointer border-2 border-emerald-500 hover:scale-105 transition"
            onClick={() =>
              setActiveId(activeId === avatar.id ? null : avatar.id)
            }
          />

          {/* Rectangle container */}
          {activeId === avatar.id && (
            <div className="absolute left-16 top-0 w-60 bg-white shadow-lg rounded-lg p-4 z-10">
              {/* Header */}
              <div className="flex items-center gap-3 mb-2">
                <img
                  src={makeAvatar(avatar.name)}
                  alt={avatar.name}
                  className="w-10 h-10 rounded-full"
                />
                <h4 className="font-bold text-gray-800">{avatar.name}</h4>
              </div>

              {/* Description */}
              <p className="text-sm text-gray-600 mb-2">
                {avatar.name} is part of our team, handling tasks efficiently and always ensuring top quality service.
              </p>

              {/* Stats */}
              <div className="flex justify-between text-sm text-gray-500 mb-2">
                <span>Projects: 12</span>
                <span>Experience: 3 yrs</span>
              </div>

              {/* Action Button */}
              <button className="w-full py-2 bg-emerald-500 text-white rounded hover:bg-emerald-600 transition">
                Contact {avatar.name}
              </button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
