import React, { useEffect, useRef, useState } from "react";

export default function Arvada() {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [service, setService] = useState(null);
  const [placesList, setPlacesList] = useState([]);
  const [query, setQuery] = useState("");
  
  // Default center (e.g. Kanpur)
  const defaultCenter = { lat: 26.449923, lng: 80.331871 };

  useEffect(() => {
    // Initialize map
    const m = new window.google.maps.Map(mapRef.current, {
      center: defaultCenter,
      zoom: 12,
    });
    setMap(m);
    setService(new window.google.maps.places.PlacesService(m));
  }, []);

  // Function to search for cleaning places
  const searchCleaningServices = () => {
    if (!service) return;

    const request = {
      query: "cleaning service " + query, // cleaning service + city or query
      fields: ["name", "geometry", "formatted_address"],
      locationBias: { radius: 50000, center: defaultCenter }, // जगह के आसपास
    };

    service.findPlaceFromQuery(request, (results, status) => {
      if (status === window.google.maps.places.PlacesServiceStatus.OK && results) {
        setPlacesList(results);

        // Clear old markers
        // Add markers for each result
        results.forEach((place) => {
          if (!place.geometry || !place.geometry.location) return;
          new window.google.maps.Marker({
            map: map,
            position: place.geometry.location,
            title: place.name,
          });
        });

        // Zoom / center to first result
        m.setCenter(results[0].geometry.location);
      } else {
        setPlacesList([]);
      }
    });
  };

  return (
    <div className="w-full h-screen flex flex-col">
      <div className="p-4 bg-white z-50">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Enter city or area to find cleaning services"
          onKeyDown={(e) => {
            if (e.key === "Enter") searchCleaningServices();
          }}
          className="border px-4 py-2 w-3/4 rounded-lg"
        />
        <button
          onClick={searchCleaningServices}
          className="ml-2 bg-blue-600 text-white px-4 py-2 rounded-lg"
        >
          Search
        </button>

        {/* Suggestions list */}
        <div className="mt-4">
          {placesList.map((place) => (
            <div
              key={place.name}
              className="cursor-pointer p-2 hover:bg-gray-100"
              onClick={() => {
                if (place.geometry && place.geometry.location) {
                  map.setCenter(place.geometry.location);
                  map.setZoom(14);
                }
              }}
            >
              <strong>{place.name}</strong>
              <p className="text-sm">{place.formatted_address}</p>
            </div>
          ))}
        </div>
      </div>

      <div ref={mapRef} className="flex-grow" />
    </div>
  );
}
