import React from "react";

const RecurringCard = ({ title, desc, img }) => (
  <div className="bg-white rounded-lg overflow-hidden shadow">
    <img src={img} alt="" className="w-full h-44 object-cover" />
    <div className="p-4">
      <h4 className="font-semibold text-teal-700">{title}</h4>
      <p className="text-gray-600 text-sm mt-1">{desc}</p>
    </div>
  </div>
);

export default function Recurring() {
  const data = [
    {
      title: "Weekly Cleaning Plans",
      desc: "Regular maintenance to keep your home pristine.",
      img: "https://images.pexels.com/photos/4239033/pexels-photo-4239033.jpeg?auto=compress&cs=tinysrgb&w=800"
    },
    {
      title: "Bi-Weekly Services",
      desc: "Perfect balance of cleanliness & cost savings.",
      img: "https://images.unsplash.com/photo-1542614472-3e0a6f0f0b7e?q=80&w=800&auto=format&fit=crop"
    },
    {
      title: "Monthly Deep Cleans",
      desc: "Thorough cleaning to refresh your entire home.",
      img: "https://images.unsplash.com/photo-1524758631624-e2822e304c36?q=80&w=800&auto=format&fit=crop"
    }
  ];

  return (
    <section className="py-16 bg-white">
      <div className="max-w-7xl mx-auto px-4">
        <div className="text-center mb-8">
          <h2 className="text-3xl sm:text-4xl font-bold text-teal-700">Recurring Cleaning</h2>
          <p className="text-gray-600 text-sm mt-2">
            Keep your home consistently clean with our recurring plans.
          </p>
        </div>

        {/* SAME GRID AS PROJECTS */}
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-3 gap-3 sm:gap-3 lg:gap-10">
          {data.map((item, idx) => (
            <RecurringCard key={idx} {...item} />
          ))}
        </div>
      </div>
    </section>
  );
}
