import React from "react";

export default function Testimonials() {
  // inline SVG data-URIs for reliable placeholder images
  const makeAvatar = (label) =>
    `data:image/svg+xml;utf8,${encodeURIComponent(
      `<svg xmlns='http://www.w3.org/2000/svg' width='160' height='160'><rect width='100%' height='100%' fill='#def7e6'/><text x='50%' y='50%' font-family='Arial, Helvetica, sans-serif' font-size='48' fill='#0f5b3f' text-anchor='middle' dominant-baseline='middle'>${label}</text></svg>`
    )}`;

  const people = [
    { id: 1, img: makeAvatar("U1"), pos: "left-[6%] top-[40px]" },
    { id: 2, img: makeAvatar("U2"), pos: "left-[18%] top-[80px]" },
    { id: 3, img: makeAvatar("U3"), pos: "left-[10%] top-[200px]" },
    { id: 4, img: makeAvatar("U4"), pos: "left-[35%] top-[70px]" },
    { id: 5, img: makeAvatar("U5"), pos: "left-[52%] top-[70px]" },
    { id: 6, img: makeAvatar("U6"), pos: "left-[25%] top-[230px]" },
    { id: 7, img: makeAvatar("U7"), pos: "right-[20%] top-[100px]" },
    { id: 8, img: makeAvatar("U8"), pos: "right-[8%] top-[140px]" }
  ];

  return (
    <section className=" max-w-7xl relative w-full bg-white py-24 overflow-visible">
      <div className="absolute inset-x-0 top-0 pointer-events-none">
        <svg viewBox="0 0 1440 120" className="w-full">
          <path fill="#e6f9ec" d="M0,64 C360,120 720,0 1080,64 C1260,96 1440,88 1440,88 L1440,0 L0,0 Z" />
        </svg>
      </div>

      <div className="max-w-[1200px] mx-auto relative px-4 sm:px-6">
        <div className="relative w-full h-[360px]">
          {people.map((p) => (
            <img
              key={p.id}
              src={p.img}
              alt={`user-${p.id}`}
              className={`w-20 h-20 md:w-24 md:h-24 rounded-full border-4 border-white shadow-lg absolute object-cover ${p.pos}`}
            />
          ))}

          <div className="absolute left-1/2 -translate-x-1/2 top-[260px] text-center">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-800">Testimonials</h2>
            <p className="text-gray-500 mt-2 max-w-lg">Our customers share amazing experiences with our cleaning services.</p>
          </div>

          <div className="absolute right-0 top-[200px] translate-x-6 md:translate-x-12 bg-[#0f5b3f] text-white p-5 md:p-6 rounded-2xl shadow-2xl max-w-sm">
            <p className="text-sm leading-relaxed">"The team was professional, thorough and respectful of my home."</p>
            <div className="mt-4 flex items-center gap-3">
              <img src={makeAvatar("CU")} alt="customer" className="w-14 h-14 rounded-full border-2 border-white object-cover" />
              <div>
                <div className="font-semibold">Carol Smith</div>
                <div className="text-sm text-yellow-300">⭐⭐⭐⭐⭐ <span className="text-white ml-1">5.0</span></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
