import { useState } from "react";

export default function Arvada() {
  const locationData = {
    kanpur: {
      city: "Kanpur",
      lat: 26.449923,
      lng: 80.331871
    },
    lucknow: {
      city: "Lucknow",
      lat: 26.846695,
      lng: 80.946167
    },
    gorakhpur: {
      city: "Gorakhpur",
      lat: 26.760555,
      lng: 83.373024
    },
    varanasi: {
      city: "Varanasi",
      lat: 25.317645,
      lng: 82.973915
    }
  };

  const [query, setQuery] = useState("");
  const [activeLocation, setActiveLocation] = useState(locationData.kanpur);

  // FILTER ONLY OBJECT CITIES
  const filteredSuggestions =
    query.trim() === ""
      ? []
      : Object.values(locationData).filter((loc) =>
          loc.city.toLowerCase().includes(query.toLowerCase())
        );

  const handleEnterSearch = () => {
    const match = Object.values(locationData).find(
      (loc) => loc.city.toLowerCase() === query.toLowerCase()
    );

    if (match) {
      setActiveLocation(match);
      return;
    }

    // custom search text → map search
    setActiveLocation({
      city: query,
      lat: query,
      lng: ""
    });
  };

  const MapFrame = ({ lat, lng, city }) => {
    const mapUrl =
      lng === ""
        ? `https://www.google.com/maps?q=${city}&z=14&output=embed`
        : `https://www.google.com/maps?q=${lat},${lng}&z=14&output=embed`;

    return (
      <iframe
        src={mapUrl}
        className="w-full h-72 sm:h-80 md:h-96 rounded-lg shadow-xl"
        loading="lazy"
      />
    );
  };

  return (
    <section className="pt-20 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-10 items-center">

          {/* TEXT SIDE */}
          <div className="order-2 md:order-1">
            <h2 className="text-3xl md:text-5xl font-bold text-teal-700 mb-6">
              Arvada, CO House Cleaning Done Right
            </h2>

            {/* SEARCH BAR */}
            <div className="relative mb-6">
              <input
                type="text"
                className="w-full border rounded-full px-4 py-2"
                placeholder="Search location…"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && handleEnterSearch()}
              />

              {/* ONLY OBJECT-BASED SUGGESTIONS */}
              {filteredSuggestions.length > 0 && (
                <div className="absolute bg-white shadow-lg rounded-lg mt-2 w-full z-10">
                  {filteredSuggestions.map((loc) => (
                    <p
                      key={loc.city}
                      className="px-4 py-2 hover:bg-gray-100 cursor-pointer"
                      onClick={() => {
                        setActiveLocation(loc);
                        setQuery("");
                      }}
                    >
                      {loc.city}
                    </p>
                  ))}
                </div>
              )}
            </div>

            <button className="bg-lime-400 text-teal-700 px-8 py-3 rounded-full font-semibold">
              Get Quote
            </button>
          </div>

          {/* MAP */}
          <div className="order-1 md:order-2">
            <MapFrame
              lat={activeLocation.lat}
              lng={activeLocation.lng}
              city={activeLocation.city}
            />
          </div>
        </div>
      </div>
    </section>
  );
}
