import { useState } from "react";

export default function Arvada() {
  const locationData = {
    kanpur: {
      city: "Kanpur",
      lat: 26.449923,
      lng: 80.331871,
    },
    lucknow: {
      city: "Lucknow",
      lat: 26.846695,
      lng: 80.946167,
    },
    gorakhpur: {
      city: "Gorakhpur",
      lat: 26.760555,
      lng: 83.373024,
    },
    varanasi: {
      city: "Varanasi",
      lat: 25.317645,
      lng: 82.973915,
    },
  };

  // Default map = Kanpur
  const [query, setQuery] = useState("");
  const [activeLocation, setActiveLocation] = useState({
    lat: locationData.kanpur.lat,
    lng: locationData.kanpur.lng,
    city: "Kanpur",
  });

  const filteredSuggestions = Object.values(locationData).filter((loc) =>
    loc.city.toLowerCase().includes(query.toLowerCase())
  );

  const updateMapFromSearch = (value) => {
    // If matches predefined cities
    const found = Object.values(locationData).find(
      (loc) => loc.city.toLowerCase() === value.toLowerCase()
    );

    if (found) {
      setActiveLocation({
        lat: found.lat,
        lng: found.lng,
        city: found.city,
      });
      return;
    }

    // If custom search typed ‚Üí show location via query search
    setActiveLocation({
      lat: value,
      lng: "",
      city: value,
    });
  };

  const MapFrame = ({ lat, lng, city }) => {
    const embedUrl =
      lng === "" // custom search
        ? `https://www.google.com/maps?q=${city}&z=14&output=embed`
        : `https://www.google.com/maps?q=${lat},${lng}&z=14&output=embed`;

    return (
      <iframe
        src={embedUrl}
        className="w-full h-72 sm:h-80 md:h-96 rounded-lg shadow-xl"
        loading="lazy"
        allowFullScreen
      ></iframe>
    );
  };

  return (
    <section className="pt-20 sm:pt-24 md:pt-28 lg:pt-32 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-10 md:gap-14 items-center">

          {/* TEXT CONTENT */}
          <div className="order-2 md:order-1">
            <h2 className="text-3xl sm:text-4xl md:text-5xl font-bold text-teal-700 mb-4 sm:mb-6 leading-tight">
              Arvada, CO House Cleaning Done Right
            </h2>

            <p className="text-gray-600 mb-4 sm:mb-6 text-base leading-relaxed">
              Our team has been serving the Arvada community for over 10 years with exceptional house cleaning services.
            </p>

            <p className="text-gray-600 mb-6 sm:mb-8 text-base leading-relaxed">
              We understand what makes Arvada homes special and treat each one with the care and attention it deserves.
            </p>

            {/* üîç SEARCH BAR */}
            <div className="relative mb-6">
              <input
                type="text"
                value={query}
                placeholder="Search location..."
                onChange={(e) => setQuery(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === "Enter") updateMapFromSearch(query);
                }}
                className="w-full border border-gray-300 rounded-full px-4 py-2 outline-none"
              />

              {/* SUGGESTIONS BOX */}
              {query.trim() !== "" && (
                <div className="absolute left-0 right-0 bg-white shadow-lg rounded-lg mt-2 z-20">
                  {filteredSuggestions.length > 0 ? (
                    filteredSuggestions.map((loc) => (
                      <p
                        key={loc.city}
                        className="px-4 py-2 hover:bg-gray-100 cursor-pointer"
                        onClick={() => {
                          setActiveLocation({
                            lat: loc.lat,
                            lng: loc.lng,
                            city: loc.city,
                          });
                          setQuery("");
                        }}
                      >
                        {loc.city}
                      </p>
                    ))
                  ) : (
                    <p className="px-4 py-2 text-gray-500">No match found</p>
                  )}
                </div>
              )}
            </div>

            <button className="bg-lime-400 text-teal-700 px-6 sm:px-8 py-3 rounded-full font-semibold text-base hover:bg-lime-300">
              Get Quote
            </button>
          </div>

          {/* MAP */}
          <div className="order-1 md:order-2">
            <MapFrame
              lat={activeLocation.lat}
              lng={activeLocation.lng}
              city={activeLocation.city}
            />
          </div>

        </div>
      </div>
    </section>
  );
}
