export default function Arvada() {

  // All cities data
  const locationData = {
    kanpur: {
      city: "Kanpur",
      lat: 26.449923,
      lng: 80.331871
    },
    lucknow: {
      city: "Lucknow",
      lat: 26.846695,
      lng: 80.946167
    },
    gorakhpur: {
      city: "Gorakhpur",
      lat: 26.760555,
      lng: 83.373024
    },
    varanasi: {
      city: "Varanasi",
      lat: 25.317645,
      lng: 82.973915
    }
  };

  // Map Component
  const MapFrame = ({ lat, lng }) => {
    const embedUrl = `https://www.google.com/maps?q=${lat},${lng}&z=14&output=embed`;

    return (
      <iframe
        src={embedUrl}
        className="w-full h-72 sm:h-80 md:h-96 rounded-lg shadow-xl"
        loading="lazy"
        allowFullScreen
      ></iframe>
    );
  };

  return (
    <section className="pt-20 sm:pt-24 md:pt-28 lg:pt-32 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">

        {/* Loop over all cities */}
        {Object.keys(locationData).map((key) => {
          const city = locationData[key];

          return (
            <div
              key={key}
              className="grid grid-cols-1 md:grid-cols-2 gap-10 md:gap-14 items-center mb-20"
            >
              {/* Text */}
              <div>
                <h2 className="text-3xl sm:text-4xl md:text-5xl font-bold text-teal-700 mb-4 sm:mb-6 leading-tight">
                  {city.city} House Cleaning Done Right
                </h2>

                <p className="text-gray-600 mb-4 sm:mb-6 text-base leading-relaxed">
                  Our team has been serving the {city.city} community for over 10 years with
                  exceptional house cleaning services.
                </p>

                <p className="text-gray-600 mb-6 sm:mb-8 text-base leading-relaxed">
                  We understand what makes {city.city} homes special and treat each one with
                  care and attention it deserves.
                </p>

                <button className="bg-lime-400 text-teal-700 px-6 sm:px-8 py-3 rounded-full font-semibold text-base hover:bg-lime-300">
                  Get Quote
                </button>
              </div>

              {/* Map */}
              <div>
                <MapFrame lat={city.lat} lng={city.lng} />
              </div>
            </div>
          );
        })}

      </div>
    </section>
  );
}
