import React, { useState } from "react";

// Example makeAvatar function
const makeAvatar = (name) =>
  `https://i.pravatar.cc/150?u=${name}`;

// Avatars array with positions
const avatars = [
  { id: 1, img: makeAvatar("U1"), name: "Carol Smith", pos: "left-[-2%] top-[140px] md:left-[6%] md:top-[140px]" },
  { id: 2, img: makeAvatar("U2"), name: "John Doe", pos: "left-[20%] top-[180px] md:left-[18%] md:top-[180px]" },
  { id: 3, img: makeAvatar("U3"), name: "Alice Lee", pos: "left-[-5%] top-[300px] md:left-[10%] md:top-[300px]" },
  { id: 4, img: makeAvatar("U4"), name: "Bob Martin", pos: "left-[15%] top-[170px] md:left-[35%] md:top-[170px]" },
  { id: 5, img: makeAvatar("U5"), name: "Emma Clark", pos: "left-[65%] top-[170px] md:left-[52%] md:top-[170px]" },
  { id: 6, img: makeAvatar("U6"), name: "David Brown", pos: "left-[40%] top-[330px] md:left-[25%] md:top-[330px]" },
  { id: 7, img: makeAvatar("U7"), name: "Sophia White", pos: "right-[5%] top-[200px] md:right-[20%] md:top-[200px]" },
  { id: 8, img: makeAvatar("U8"), name: "Liam Scott", pos: "right-[-15%] top-[180px] md:right-[5%] md:top-[180px]" },
];

export default function AvatarShowcase() {
  const [activeId, setActiveId] = useState(null);

  return (
    <div className="w-full bg-gray-100 py-10">
      {/* Centered max-width container */}
      <div className="relative max-w-6xl mx-auto h-[700px] px-6">
        
        {/* Centered Testimonials Heading */}
        <div className="flex flex-col items-center text-center mb-12">
          <h2 className="text-2xl md:text-4xl font-bold text-gray-800">Testimonials</h2>
          <p className="text-gray-500 mt-2 max-w-2xl px-4">
            Our customers share amazing experiences with our cleaning services.
          </p>
        </div>

        {/* Avatars */}
        {avatars.map((avatar) => (
          <div key={avatar.id} className={`absolute ${avatar.pos}`}>
            <img
              src={avatar.img}
              alt={avatar.name}
              className="w-12 h-12 rounded-full cursor-pointer border-2 border-emerald-500 hover:scale-105 transition"
              onClick={() =>
                setActiveId(activeId === avatar.id ? null : avatar.id)
              }
            />

            {/* Testimonial Container next to avatar */}
            {activeId === avatar.id && (
              <div className="absolute left-14 top-0 bg-[#0f5b3f] text-white p-5 md:p-6 
                              rounded-2xl shadow-2xl max-w-[280px] md:max-w-[320px]">

                {/* Triangle pointing to avatar */}
                <div className="absolute -left-3 top-4 w-0 h-0 border-t-[8px] border-t-transparent 
                                border-b-[8px] border-b-transparent border-r-[10px] border-r-[#0f5b3f]"></div>

                {/* Testimonial Text */}
                <p className="text-sm md:text-base leading-snug">
                  "The team was professional, thorough and respectful of my home."
                </p>

                {/* Name + Stars */}
                <div className="mt-4 flex flex-col gap-1">
                  <div className="font-semibold text-base md:text-lg">{avatar.name}</div>
                  <div className="text-sm md:text-base text-yellow-300">
                    ⭐⭐⭐⭐⭐ <span className="text-white ml-1">5.0</span>
                  </div>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
